import React from 'react';
import AuthService from './auth-service';
import {Link, withRouter} from 'react-router-dom';
class Signup extends React.Component{

    constructor(props){
        super(props);
        this.state={
            firstname:"",
            lastname:"",
            phonenumber:"",
            email:"",
            password:""
        };
        this.service=new AuthService()
    }

    handleFormSubmit=(event)=>{
        event.preventDefault();

        const firstname=this.state.firstname;
        const lastname=this.state.lastname;
        const phonenumber=this.state.phonenumber;
        const email=this.state.email;
        const password=this.state.password;


        this.service.signup(firstname, lastname,phonenumber,email,password)
        .then(response=>{
            this.setState({
                firstname:"",
                lastname:"",
                phonenumber:"",
                email:"",
                password:""
            })
            this.props.getUser(response);
            //console.log("response sign up",response)
            this.props.history.push("/profile");
        })
        .catch(err=>console.log(err))
    }
    handleChange=(event)=>{
        const {name,value}=event.target;
        this.setState({[name]:value})
    }

    render(){

        return(
            <div>
                <form onSubmit={this.handleFormSubmit}>

                    <div>
                        <label>First Name:</label>
                        <input type="text" name="firstname" value={this.state.firstname} onChange={e=>this.handleChange(e)}/>
                    </div>

                    <div>
                        <label>Password:</label>
                        <input type="password" name="password" value={this.state.password} onChange={e=>this.handleChange(e)}/>
                    </div>

                    <div>
                        <label>Campus:</label>
                        <select name="campus" value={this.state.campus} onChange={e=>this.handleChange(e)}>
                            <option value="Madrid">Madrid</option>
                            <option value="Barcelona">Barcelona</option>
                            <option value="Miami">Miami</option>
                            <option value="Paris">Paris</option>
                            <option value="Berlin">Berlin</option>
                            <option value="Amsterdam">Amsterdam</option>
                            <option value="México">México</option>
                            <option value="Sao Paulo">Sao Paulo</option>
                        </select>
                    </div>

                    <div>
                        <label>Course:</label>
                        <select name="course" value={this.state.course} onChange={e=>this.handleChange(e)}>
                            <option value="WebDev'">WebDev</option>
                            <option value="UX/UI">UX/UI</option>
                            <option value="Data Analytics">Data Analytics</option>
                        </select>
                    </div>

                    <button>Create the account</button>
                </form>
                <p>
                    Already have account ?
                    <Link to={"/login"}>Log In</Link>
                </p>
                <Link to={"/"}>Home</Link>
            </div>
        )
    }
}
export default withRouter(Signup);
