import React from 'react';
import {Link} from 'react-router-dom';
import AuthService from './auth/auth-service.js'
class Profile extends React.Component{
    constructor(props){
        super(props);
        this.service=new AuthService()
    }
    logoutUser=()=>{
        this.service.logout()
        .then(()=>this.props.getUser(null))
    }
    render(){
        return(
            <div>
                {this.props.userInSession?
                    <div>
                        <h2>Profile</h2>
                        <div className="field">
                            <div className="field-name">Username</div>
                            <div>{this.props.userInSession.username}</div>
                        </div>
                        <div className="field">
                            <div className="field-name">Campus</div>
                            <div>{this.props.userInSession.campus}</div>
                        </div>
                        <div className="field">
                            <div className="field-name">Course</div>
                            <div>{this.props.userInSession.course}</div>
                        </div>
                    </div>
                    :
                    <div>Please log in to see your account<Link to="/login">Log In</Link></div> 
                }

                <button onClick={()=>this.logoutUser()}>Logout</button>
                <Link to="/">Home</Link>       
            </div>
        )
    }
}
export default Profile;
