import React from 'react';
import NavBar from './NavBar.js';
import {Link} from 'react-router-dom';
import StaticDatePicker from './Calendar.js'

class AvailabilityDates extends React.Component{
    state={
       Date:new Date()
    }

    getDate = date => {
        this.setState({
           Date:date
        })
    };
    handleDisableDates=(date)=>{
        console.log(this.props.AvDates[0])
        console.log(new Date(date).toISOString())
        return !this.props.AvDates.includes(new Date(date).toISOString())
    }
    render(){
        return(
            <div>
                <NavBar/>
                <StaticDatePicker disableDates={this.handleDisableDates} getDate={this.getDate}/>
                {this.props.AvDates.map((AvDate,index)=>{
                    return(
                        <div key={index}>
                            {AvDate}
                        </div>
                    )
                })}
                <Link to="/booking/availability-request">Change dates for availability research</Link>
            </div>
        )
    }
}
export default AvailabilityDates;
