import React from 'react';
import NavBar from './NavBar.js';
import {Link} from 'react-router-dom';
import StaticDatePicker from './Calendar.js'

class AvailabilityDates extends React.Component{
    handleDisableDate=(date)=>{
        return (
            this.props.AvDates.map(AvDate=>date!==AvDate)
        )
    }
    render(){
        return(
            <div>
                <NavBar/>
                <StaticDatePicker disableDates={this.handleDisableDate}/>
                {this.props.AvDates.map((AvDate,index)=>{
                    return(
                        <div key={index}>
                            {AvDate}
                        </div>
                    )
                })}
                <Link to="/booking/availability-request">Change dates for availability research</Link>
            </div>
        )
    }
}
export default AvailabilityDates;
