import React from 'react';
import AuthService from './auth-service';
import {Link, withRouter} from 'react-router-dom';
class Signup extends React.Component{

    constructor(props){
        super(props);
        this.state={
            username:"",
            password:"",
            campus:[],
            course:[],
            image:""
        };
        this.service=new AuthService()
    }

    handleFormSubmit=(event)=>{
        event.preventDefault();

        const username=this.state.username;
        const password=this.state.password;
        const campus=this.state.campus;
        const course=this.state.course;
        const image=this.state.image;
        console.log("campus",campus,"course",course,"image",image)
        this.service.signup(username,password,campus,course,image)
        .then(response=>{
            this.setState({
                username:"",
                password:"",
                campus:[],
                course:[],
                image:""
            })
            this.props.getUser(response);
            console.log("response sign up",response)
            this.props.history.push("/");
        })
        .catch(err=>console.log(err))
    }
    handleChange=(event)=>{
        const {name,value}=event.target;
        this.setState({[name]:value})
    }
    handleChangeCampus=(event)=> {
        this.setState({campus: Array.from(event.target.selectedOptions, (item) => item.value)});
      }
    handleChangeCourse=(event)=> {
        this.setState({course: Array.from(event.target.selectedOptions, (item) => item.value)});
      }
    render(){

        return(
            <div>
                <form onSubmit={this.handleFormSubmit}>
                    <label>Username:</label>
                    <input type="text" name="username" value={this.state.username} onChange={e=>this.handleChange(e)}/>
                    <label>Password:</label>
                    <input type="password" name="password" value={this.state.password} onChange={e=>this.handleChange(e)}/>
                    <label>Campus:</label>
                    <select name="campus" multiple={true} value={this.state.campus} onChange={e=>this.handleChangeCampus(e)}>
                        <option value="Madrid">Madrid</option>
                        <option value="Barcelona">Barcelona</option>
                        <option value="Miami">Miami</option>
                        <option value="Paris">Paris</option>
                        <option value="Berlin">Berlin</option>
                        <option value="Amsterdam">Amsterdam</option>
                        <option value="México">México</option>
                        <option value="Sao Paulo">Sao Paulo</option>
                    </select>
                    <label>Course:</label>
                    <select name="course" multiple={true} value={this.state.course} onChange={e=>this.handleChangeCourse(e)}>
                        <option value="WebDev'">WebDev</option>
                        <option value="UX/UI">UX/UI</option>
                        <option value="Data Analytics">Data Analytics</option>
                    </select>
                    <label>Image:</label>
                    <input type="text" name="image" value={this.state.image} onChange={e=>this.handleChange(e)}/>

                    <button>Submit</button>
                </form>
                <p>
                    Already have account ?
                    <Link to={"/login"}>Log In</Link>
                </p>
                <Link to={"/"}>Home</Link>
            </div>
        )
    }
}
export default withRouter(Signup);
