import React from 'react';
import AuthService from './auth-service';
import {Link, withRouter} from 'react-router-dom';
class Signup extends React.Component{

    constructor(props){
        super(props);
        this.state={
            username:"",
            password:"",
            campus:[],
            course:[],
            image:""
        };
        this.service=new AuthService()
    }

    handleFormSubmit=(event)=>{
        event.preventDefault();

        const username=this.state.username;
        const password=this.state.password;
        const campus=this.state.campus;
        const course=this.state.course;
        const image=this.state.image;
        this.service.signup(username,password,campus,course,image)
        .then(response=>{
            this.setState({
                username:"",
                password:"",
                campus:"",
                course:"",
                image:""
            })
            this.props.getUser(response);
            console.log("response sign up",response)
            this.props.history.push("/");
        })
        .catch(err=>console.log(err))
    }
    handleChange=(event)=>{
        const {name,value}=event.target;
        this.setState({[name]:value})
    }
    render(){
        return(
            <div>
                <form onSubmit={this.handleFormSubmit}>
                    <label>Username:</label>
                    <input type="text" name="username" value={this.state.username} onChange={e=>this.handleChange(e)}/>
                    <label>Password:</label>
                    <input type="password" name="password" value={this.state.password} onChange={e=>this.handleChange(e)}/>
                    <label>Campus:</label>
                    <select name="campus" multiple size="8" onChange={e=>this.handleChange(e)}>
                        <option value={this.state.campus}>Madrid</option>
                        <option value={this.state.campus}>Barcelona</option>
                        <option value={this.state.campus}>Miami</option>
                        <option value={this.state.campus}>Paris</option>
                        <option value={this.state.campus}>Berlin</option>
                        <option value={this.state.campus}>Amsterdam</option>
                        <option value={this.state.campus}>México</option>
                        <option value={this.state.campus}>Sao Paulo</option>
                    </select>

                    <button>Submit</button>
                </form>
                <p>
                    Already have account ?
                    <Link to={"/login"}>Log In</Link>
                </p>
                <Link to={"/"}>Home</Link>
            </div>
        )
    }
}
export default withRouter(Signup);
